const gulp = require('gulp');
const plumber = require('gulp-plumber');
/**
 * This task will copy sections folder into 'dist'.
 */
const sections = function(config) {
  return function() {
    return gulp
      .src([ './src/sections/*.html' ])
      .pipe(plumber())
      .pipe(gulp.dest(config.distpath + '/sections'));
  };
};

module.exports = sections;
