/* eslint-env browser, jquery */
import $ from 'jquery';
import { bus } from 'partybus';


$(document).on('click', '[data-nav]', (event) => {
  const next = $(event.target).closest('[data-nav]').attr('data-nav');
  bus.emit('hucklebuck', next);
});

$(document).on('click', '#next-up', (event) => {
  const next = $(event.target).closest("[data-next-up]").attr('data-next-up');
  bus.emit('hucklebuck', next);
});

$(document).on('click', '.cust-btn', (event) => {
  const $target = $(event.currentTarget);
  const next = $target.closest("[data-target]").attr('data-target');
  bus.emit('hucklebuck', next);
});

$(document).on('click', '.hucklebuck-next', () => {
  bus.emit('hucklebuck', 'next');
});

$(document).on('click', '.hucklebuck-prev', () => {
  bus.emit('hucklebuck', 'prev');
});

$(document).on('touchstart', 'a', (event) => {
  $(event.target).trigger('click');
});
