import { Selector } from 'testcafe';

export class ModalDialog {
  constructor(selector) {
    this.mainElement = selector;

    this.button = this.mainElement.find('button').withText('button');
    this.demLinks = this.mainElement.find('div.modal-body li a');
    this.closeButton = this.mainElement.find('button.close');
  }

  isVisible() {
    return this.mainElement.visible;
  }
}

export class HucklebuckDemoPage {
  constructor() {
    this.sectionMenuLaunch = Selector('a[data-target="#menu"]');
    this.sectionMenuModal = new ModalDialog(Selector('#menu.modal'));
    this.popoverTitle = Selector('h3.popover-title');
    this.tooltip = Selector('.tooltip');
  }
}
